import React, { useState } from 'react';
import useStats from '../utils/useStats';
import Stats from './Stats';

const CountrySelector = () => {
  const countries = useStats('https://covid19.mathdro.id/api/countries');
  console.log(queryCountry);
  const [selectedCountry, setSelectedCountry] = useState(null);
  let queryCountry = selectedCountry;
  if (!countries) return <p>Loading...</p>;
  return (
    <div>
      <select style={{ width: 400 }}>
        {Object.entries(countries.countries).map(([code, country]) => (
          <option
            key={code}
            value={code}
            onSelect={(e) => setSelectedCountry(e.target.value)}
          >
            {country.name}
          </option>
        ))}
      </select>
      <Stats url={`https://covid19.mathdro.id/api/countries/${queryCountry}`} />
    </div>
  );
};

export default CountrySelector;
