import { useState, useEffect } from 'react';

const useStats = (url) => {
  const [stats, setStats] = useState();

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      const data = await fetch(url)
        .then((res) => res.json())
        .catch((err) => setError(err));
      setStats(data);
      setLoading(false);
    };
    fetchData();
  }, [url]);
  return stats;
};

export default useStats;
