import { createGlobalStyle } from 'styled-components';
import '../components/CountrySelector.css';

const GlobalStyle = createGlobalStyle`
*{
  margin:0;
  padding:0;
  box-sizing:border-box;
}

html {
font-family:--apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
}

body {
  width:100vw;
  padding-bottom: 18px;
  min-height: 100vh;
  position:relative;
  overflow:hidden;
  background-color: rgba(0,0,0, 0.1);
}

header {
  width:80%;
  text-align: center;
  justify-items:center;
  justify-content:center;
  align-items:center;
  margin:2rem auto;
}

main {
  width:80%;
  text-align: center;
  justify-items:center;
  justify-content:center;
  align-items:center;
  margin:1rem auto;
}

footer {
  position: absolute;
  text-align: center;
  justify-items:center;
  justify-content:center;
  align-items:center;
  margin:1rem auto;
  background: #333;
  color: #fff;
  padding: 18px;
  bottom: 0;
  width: 100%;
}

select:hover {
  cursor: pointer;
}
`;
