import React from 'react';
import Stats from '../components/Stats';
import CountrySelector from '../components/CountrySelector';
import { createGlobalStyle } from 'styled-components';
import Head from 'next/head';

const GlobalStyle = createGlobalStyle`
*{
  margin:0;
  padding:0;
  box-sizing:border-box;
}

html {
font-family:--apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
}

body {
  width:100vw;
  padding-bottom: 18px;
  min-height: 100vh;
  position:relative;
}

footer {
  position: absolute;
  text-align: center;
  background: #333;
  color: #fff;
  padding: 18px;
  bottom: 0;
  width: 100%;
}
`;

export default function IndexPage() {
  let date = new Date();
  let yearNow = date.getFullYear();
  let dateNow = date.toLocaleDateString('sr-RS');
  return (
    <div>
      <Head>
        <title>КОРОНА ВИРУС - COVID 19 статистика</title>
        <meta
          property="og:title"
          content="КОРОНА ВИРУС (COVID 19) статистика по земљама"
          key="title"
          description="статистика по земљама о корона вирусу COVID 19"
        />
      </Head>
      <GlobalStyle />
      <header>
        <h1>КОРОНА ВИРУС (COVID 19)</h1>
        <h4>
          статистика по земљама на дан <span>{dateNow}</span>
        </h4>
      </header>
      <main>
        <CountrySelector />
        <h2></h2>
        <Stats url="https://covid19.mathdro.id/api" />
      </main>
      <footer>
        Copyright &copy; <span>{yearNow}</span>, Stefan Ljiljak
      </footer>
    </div>
  );
}
